mapper.insert:
INSERT INTO chatRoom
SET roomName = #{roomName},
memberId = #{memberId};

INSERT INTO chat_user
SET roomId = #{roomId},
memberId = #{memberId};

mapper.delete:
DELETE FROM chat_user
WHERE roomId = #{roomId}
AND memberId = #{memberId};

mapper.select:
SELECT m.name AS 'userName'
FROM chat_user cu
INNER JOIN `member` m
ON m.id = cu.memberId
WHERE roomId = #{roomId}
AND memberId = #{memberId};

SELECT *
FROM chat_user
WHERE memberId = #{memberId}
AND roomId = #{roomId};
