mapper.delete:
DELETE FROM chat_user
WHERE roomId = #{roomId}
AND
memberId = #{memberId}
AND roomType = #{roomType};

mapper.select:
SELECT m.name AS 'userName'
FROM chat_user cu
INNER JOIN `member` m
ON m.id = cu.memberId
WHERE
roomId = #{roomId}
AND memberId = #{memberId}
AND roomType = #{roomType};

SELECT *
FROM chat_user
WHERE memberId = #{memberId}
AND
roomId = #{roomId}
AND roomType = #{roomType};

SELECT *
FROM chat
WHERE roomId = #{roomId}
AND roomType = #{roomType}
AND type = 'TALK';

SELECT COUNT(c.id) 
FROM chat c
INNER JOIN PersonalchatRoom pcr
ON c.roomId = pcr.id
WHERE c.roomId = #{roomId}
AND c.roomType = #{roomType}
AND c.id > #{lastReadId}
AND (pcr.memberId1 = #{memberId} OR pcr.memberId2 = #{memberId})
AND c.`type` = 'talk';

SELECT COUNT(id) 
FROM chat
WHERE roomId = #{roomId}
AND roomType = #{roomType}
AND id > #{lastReadId}
AND `type` = 'talk';

SELECT IFNULL(MAX(lastReadId), 0) AS lastReadId
FROM read_chat
WHERE memberId = #{memberId}
AND roomId = #{roomId}
AND roomType = #{roomType};
